package qin.test;import org.junit.Test;import qin.javaee8.core.JavaEE8BaseSupport;/** * Created by Administrator on 2016/10/18 10-018-18.<br/> */public class FindJobPersonHTMLTest implements JavaEE8BaseSupport{    @Test    public void makeUITest()    {        j8Tools.superInfo(                  makeUI                            (                                      "运营", "UI/UE/平面设计", "IT质量管理",                                      "配置管理", "IT管理", "IT项目管理",                                      "IT运维/技术支持", "高级管理", "人力资源",                                      "财务/审计/税务", "市场", "公关/媒介",                                      "销售/客服/技术支持", "法务", "行政/后勤/文秘",                                      "项目管理/项目协调"                            )        );    }    private String makeUI(String... spanName)    {        StringBuilder sb = new StringBuilder();        for (int i = 0; i < spanName.length; i++)        {            sb                      .append("<ul>\n")                      .append("    <span>")                      .append(spanName[i])                      .append("</span>\n")                      .append("    <ul>\n")                      .append("        <li></li>\n")                      .append("    </ul>\n")                      .append("</ul>\n\n");        }        return sb.toString();    }}